mapper.select:
SELECT 

FROM school a

WHERE a.id = #{id}

SELECT
FROM school a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

SELECT 

FROM school a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE school SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
